import React, { useState, useEffect } from 'react';
import { Redirect, Route, Link } from 'react-router-dom';
import { Switch } from 'react-router-dom';        //modified before it was react-router
import { withRouter } from 'react-router-dom';
import { Button, Navbar, Nav, Form, FormControl, Container,NavLink} from 'react-bootstrap';


function simulateNetworkRequest() {
  return new Promise((resolve) => setTimeout(resolve, 2000));
}

function LoadingButton() {
  const [isLoading, setLoading] = useState(false);

  useEffect(() => {
    if (isLoading) {
      simulateNetworkRequest().then(() => {
        setLoading(false);
      });
    }
  }, [isLoading]);

  const handleClick = () => setLoading(true);

  return (
    <Button
      variant="primary"
      disabled={isLoading}
      onClick={!isLoading ? handleClick : null}
    >
      {isLoading ? 'Loading…' : 'Login'}
    </Button>
  );
}



class NavBar extends React.Component {

  constructor(props) {
    super(props);
}

  render() {
    return <>

      <Navbar bg="primary" variant="dark" fixed="top" expand="lg">
        <Navbar.Brand href="#home">Noleggio Veicoli</Navbar.Brand>
        <Nav className="ml-md-auto">
          {this.state.isLogged && 
            <>
              <Navbar.Brand>Welcome {this.state.authUser.name}!</Navbar.Brand> 
              <Nav.Link onClick = {() => {props.logoutUser()}}>Logout</Nav.Link>
            </>}
          {!this.state.isLogged && <Nav.Link as = {NavLink} to = "/login"><LoadingButton /></Nav.Link>}
        </Nav>
      </Navbar>

    </>
  }

}

export default NavBar;
