import React, { useState, useEffect } from 'react';
import { Redirect, Route, Link } from 'react-router-dom';
import { Switch } from 'react-router-dom';        //modified before it was react-router
import { withRouter } from 'react-router-dom';
import { Button, ButtonGroup, Navbar, Nav, Form, FormControl, Container, Row, Col, NavItem, Modal } from 'react-bootstrap';
import ListGroup from 'react-bootstrap/ListGroup';
import { NavLink } from 'react-router-dom';
//import Sidebar from 'react-bootstrap-sidebar';
import API from './.././API/API'

function CategoryRow(props) {
    return (
        <Button variant="outline-primary">{props.category}</Button>
    )
}

function BrandRow(props) {
    return (
    <ListGroup.Item action active={props.brandsFilters.includes(props.brand) ? true :false} 
    onClick={() => props.addOrRemoveBrandsFilters(props.brand)}>{props.brand}</ListGroup.Item>
    )
}

function SideBar(props) {
    return <>
        <h2>Brands</h2>
        <ListGroup variant="flush"> 
            {props.brands.map((b) => <BrandRow brand={b.brand} brandsFilters={props.brandsFilters}/>)}
        </ListGroup>
        <h2>Categories</h2>
        <ButtonGroup vertical>
        {props.categories.map((b) => <CategoryRow category={b} />)}
        </ButtonGroup>
    </>

}

export default SideBar;
