import React from 'react';
import { useState } from "react";

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Alert from 'react-bootstrap/Alert';
import { AuthContext } from '../auth/AuthContext'
import { Redirect, Route, Link, } from 'react-router-dom';
import { Button, Navbar, Nav, Form, FormControl, Container,NavLink } from 'react-bootstrap';

function LoginForm (props){

        const [username,setUsername] = useState('');
        const [password,setPassword] = useState('');
        const [submitted,setSubmitted] = useState(false)
        /*
        this.state.loginMethod = props.loginMethod;
        this.state = { username: '', password: '', submitted: false };
    */
    /*
    onChangeUsername = (event) => {
        this.setState({ username: event.target.value });
    };*/
    const onChangeUsername = (event) => {
        setUsername(event.target.value );
    }

    /*
    onChangePassword = (event) => {
        this.setState({ password: event.target.value });
    };*/
    const onChangePassword = (event) => {
        setPassword(event.target.value);
    }


    /*
    handleSubmit = (event, onLogin) => {
        event.preventDefault();
        onLogin(this.state.username, this.state.password);
        this.setState({ submitted: true });
    }*/

    const handleSubmit = (event, onLogin) => {
        event.preventDefault();
        onLogin(username, password);
        setSubmitted(true);
    }



    /*render() {
        if (this.state.submitted)
            return <Redirect to='/configurator' />;*/
        return (
            <>
                <Container fluid>
                    <Row>
                        <Col>
                            <h2>
                                <div className="content">
                                    Log-in to your account
                                </div>
                            </h2>

                            <Form method="POST" onSubmit={(event) => this.handleSubmit(event, props.loginMethod())}>
                                <Form.Group controlId="username">
                                    <Form.Label>E-mail</Form.Label>
                                    <Form.Control type="email" name="email" placeholder="E-mail" value={this.state.username} onChange={(ev) => onChangeUsername(ev)} required autoFocus />
                                </Form.Group>

                                <Form.Group controlId="password">
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control type="password" name="password" placeholder="Password" value={this.state.password} onChange={(ev) => onChangePassword(ev)} required />
                                </Form.Group>

                                <Button variant="primary" type="submit">Login</Button>
                                <Nav.Link as = {NavLink} href= "/cars">Cancel</Nav.Link>

                            </Form>



                        </Col>
                    </Row>
                </Container>
            </>
        )


    }



export default LoginForm;
